import React, { useState } from 'react';
import axios from 'axios';

const GraphQuery = ({ apiEndpoint = 'http://localhost:5001/api/run-cypher', neo4jConfig }) => {
  const [query, setQuery] = useState('');
  const [result, setResult] = useState([]);

  // Function to handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(apiEndpoint, { query });
      setResult(response.data);
    } catch (error) {
      console.error('Error executing Cypher query:', error);
    }
  };

  const handleVisualizeClick = () => {
    const newWindow = window.open('', '_blank', 'width=800,height=600');
    if (newWindow) {
      // Write the HTML structure to the new window
      newWindow.document.write(`
        <html>
          <head>
            <title>Graph Visualization</title>
            <style>
              body {
                margin: 0;
                padding: 0;
                overflow: hidden;
              }
              #graph {
                width: 100%;
                height: 100vh;
              }
            </style>
            <script src="https://cdnjs.cloudflare.com/ajax/libs/neovis.js/2.0.0/neovis.min.js"></script>
          </head>
          <body>
            <div id="graph"></div>
            <script>
              const neo4jConfig = {
                url: '${neo4jConfig.url}',
                username: '${neo4jConfig.username}',
                password: '${neo4jConfig.password}',
                database: '${neo4jConfig.database}',
              };
              const query = '${query}';

              const neo4jVis = new NeoVis({
                container_id: 'graph',
                server_url: neo4jConfig.url,
                server_user: neo4jConfig.username,
                server_password: neo4jConfig.password,
                database: neo4jConfig.database,
                initial_cypher: query,
              });

              neo4jVis.render();
            </script>
          </body>
        </html>
      `);
      newWindow.document.close();
    }
  };

  const renderResult = () => {
    if (result.length === 0) return <p>No Results to Display</p>;

    return result.map((record, index) => (
      <div key={index} style={{ border: '1px solid #ddd', padding: '10px', marginBottom: '10px' }}>
        {Object.keys(record).map((key, i) => {
          const value = record[key];
          return (
            <div key={i}>
              <strong>{key}:</strong> {renderValue(value)}
            </div>
          );
        })}
      </div>
    ));
  };

  const renderValue = (value) => {
    if (value.labels) {
      return (
        <div>
          <strong>Node Labels:</strong> {value.labels.join(', ')}
          <br />
          <strong>Properties:</strong> {JSON.stringify(value.properties, null, 2)}
        </div>
      );
    } else if (value.type) {
      return (
        <div>
          <strong>Relationship Type:</strong> {value.type}
          <br />
          <strong>Properties:</strong> {JSON.stringify(value.properties, null, 2)}
        </div>
      );
    } else if (value.segments) {
      return (
        <div>
          <strong>Path:</strong>
          {value.segments.map((segment, index) => (
            <div key={index}>
              <em>Start Node:</em> {JSON.stringify(segment.start.properties, null, 2)} <br />
              <em>Relationship:</em> {segment.relationship.type} <br />
              <em>End Node:</em> {JSON.stringify(segment.end.properties, null, 2)} <br />
            </div>
          ))}
        </div>
      );
    } else {
      return <pre>{JSON.stringify(value, null, 2)}</pre>;
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Neo4j Graph Query Interface</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Cypher Query:
          <textarea
            style={{ width: '100%', height: '100px' }}
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="MATCH (n) RETURN n LIMIT 10"
          />
        </label>
        <br />
        <button type="submit">Run Query</button>
        <button type="button" onClick={handleVisualizeClick}>
          Visualize Graph
        </button>
      </form>

      {/* Display Query Results */}
      <h2>Results:</h2>
      {renderResult()}
    </div>
  );
};

export default GraphQuery;










